    SecondNum = display.value;
    switch (typeOp) {
        case "add":
            result = parseFloat(firstNum) + parseFloat(SecondNum);
            firstNum = result.toString();
            break;
        case "less":
            result = parseFloat(firstNum) - parseFloat(SecondNum);
            firstNum = result.toString();
            break;
        case "mult":
            result = parseFloat(firstNum) * parseFloat(SecondNum);
            firstNum = result.toString();
            break;
        case "div":
            result = parseFloat(firstNum) / parseFloat(SecondNum);
            firstNum = result.toString();
            break;
        case "percentage":
            result = parseFloat(firstNum) * parseFloat(SecondNum) / 100;
